CREATE OR REPLACE FUNCTION insertUserTrigger() RETURNS TRIGGER AS $$
begin
	IF(TG_OP = 'INSERT') THEN
		NEW.usr_insert_dt = now ();
	ELSEIF ( TG_OP = 'UPDATE' ) THEN
		NEW.usr_update_dt = now ();
	END IF;
RETURN NEW;
end
$$ LANGUAGE plpgsql;
DROP TRIGGER IF EXISTS user_insert_trigger ON users;
CREATE TRIGGER user_insert_trigger BEFORE INSERT OR UPDATE ON users FOR EACH ROW EXECUTE PROCEDURE insertUserTrigger();





