		NEW.usr_insert_dt = now ();

	ELSEIF ( TG_OP = 'UPDATE' ) THEN

		NEW.usr_update_dt = now ();

	END IF;

	NEW.usr_name_ch = unaccent ( upper ( NEW.usr_name_ch ) );

RETURN NEW;

end

$BODY$

  LANGUAGE plpgsql VOLATILE
