-- Function: verify_document()

DROP FUNCTION if exists verify_document() cascade;

CREATE OR REPLACE FUNCTION verify_document()
  RETURNS trigger AS
$BODY$
DECLARE
    isUniq boolean;
    doc_id user_document.udc_code_ch%TYPE;
begin
	IF (TG_OP = 'INSERT') OR  ( (TG_OP = 'INSERT') AND ( NEW.udc_code_ch <> OLD.udc_code_ch ) ) THEN
		SELECT doc_uniq_bt INTO isUniq FROM document_type WHERE doc_id_in = NEW.doc_id_in;
		IF ( isUniq ) THEN
			SELECT udc_code_ch INTO doc_id FROM user_document WHERE udc_code_ch = NEW.udc_code_ch;
			IF FOUND THEN
				RAISE EXCEPTION 'Unique trigger constraint violation. % alread exists', NEW.udc_code_ch;
			END IF;
		END IF;
	END IF;
	IF NEW.doc_id_in = 6 THEN
		NEW.udc_code_ch = unaccent ( lower ( NEW.udc_code_ch ) );
	END IF;
RETURN NEW;
end
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION verify_document()
  OWNER TO dba_cloud;


 DROP TRIGGER if exists user_document_update_trg ON user_document;

CREATE TRIGGER user_document_update_trg
  BEFORE INSERT OR UPDATE
  ON user_document
  FOR EACH ROW
  EXECUTE PROCEDURE verify_document();






